// Схема валидации для формы обновления профиля
const ProfileSchema = Yup.object().shape({
  username: Yup.string()
    .required('Обязательное поле')
    .min(3, 'Минимум 3 символа')
    .max(20, 'Максимум 20 символов'),
  email: Yup.string()
    .email('Некорректный email')
    .required('Обязательное поле')
});

// Схема валидации для формы изменения пароля
const PasswordSchema = Yup.object().shape({
  currentPassword: Yup.string()
    .required('Обязательное поле'),
  newPassword: Yup.string()
    .required('Обязательное поле')
    .min(6, 'Пароль должен содержать минимум 6 символов'),
  confirmPassword: Yup.string()
    .required('Подтвердите пароль')
    .oneOf([Yup.ref('newPassword'), null], 'Пароли должны совпадать')
});

const ProfilePage = () => {
  const { user, updateProfile, changePassword, logout } = useAuth();
  const [activeTab, setActiveTab] = useState('profile');
  const [status, setStatus] = useState({
    message: '',
    success: false
  });

  if (!user) {
    return <div>Загрузка данных пользователя...</div>;
  }

  const handleUpdateProfile = async (values, { setSubmitting }) => {
    setStatus({ message: '', success: false });

    try {
      const success = await updateProfile({
        username: values.username,
        email: values.email
      });

      if (success) {
        setStatus({
          message: 'Профиль успешно обновлен',
          success: true
        });
      } else {
        setStatus({
          message: 'Не удалось обновить профиль',
          success: false
        });
      }
    } catch (error) {
      console.error('Ошибка при обновлении профиля:', error);
      setStatus({
        message: error.response?.data?.message || 'Ошибка при обновлении профиля',
        success: false
      });
    }

    setSubmitting(false);
  };

  const handleChangePassword = async (values, { setSubmitting, resetForm }) => {
    setStatus({ message: '', success: false });

    try {
      const success = await changePassword({
        currentPassword: values.currentPassword,
        newPassword: values.newPassword
      });

      if (success) {
        setStatus({
          message: 'Пароль успешно изменен',
          success: true
        });
        resetForm();
      } else {
        setStatus({
          message: 'Не удалось изменить пароль',
          success: false
        });
      }
    } catch (error) {
      console.error('Ошибка при изменении пароля:', error);
      setStatus({
        message: error.response?.data?.message || 'Ошибка при изменении пароля',
        success: false
      });
    }

    setSubmitting(false);
  };

  return (
    <Container>
      <Title>Личный кабинет</Title>

      <Tabs>
        <Tab 
          active={activeTab === 'profile'} 
          onClick={() => setActiveTab('profile')}
        >
          Мой профиль
        </Tab>
        <Tab 
          active={activeTab === 'security'} 
          onClick={() => setActiveTab('security')}
        >
          Безопасность
        </Tab>
        <Tab 
          active={activeTab === 'bookings'} 
          onClick={() => setActiveTab('bookings')}
        >
          Мои бронирования
        </Tab>
      </Tabs>

      {activeTab === 'profile' && (
        <Card>
          <CardTitle>Информация о пользователе</CardTitle>

          {status.message && activeTab === 'profile' && (
            <Alert success={status.success}>
              {status.message}
            </Alert>
          )}

          <Formik
            initialValues={{
              username: user.username || '',
              email: user.email || ''
            }}
            validationSchema={ProfileSchema}
            onSubmit={handleUpdateProfile}
          >
            {({ isSubmitting }) => (
              <StyledForm>
                <FormGroup>
                  <Label htmlFor="username">Имя пользователя</Label>
                  <Input
                    type="text"
                    id="username"
                    name="username"
                    placeholder="Введите имя пользователя"
                  />
                  <ErrorMessage name="username" component={Error} />
                </FormGroup>

                <FormGroup>
                  <Label htmlFor="email">Email</Label>
                  <Input
                    type="email"
                    id="email"
                    name="email"
                    placeholder="Введите email"
                  />
                  <ErrorMessage name="email" component={Error} />
                </FormGroup>

                <Button type="submit" disabled={isSubmitting}>
                  {isSubmitting ? 'Сохранение...' : 'Сохранить изменения'}
                </Button>
              </StyledForm>
            )}
          </Formik>
        </Card>
      )}

      {activeTab === 'security' && (
        <Card>
          <CardTitle>Изменить пароль</CardTitle>

          {status.message && activeTab === 'security' && (
            <Alert success={status.success}>
              {status.message}
            </Alert>
          )}

          <Formik
            initialValues={{
              currentPassword: '',
              newPassword: '',
              confirmPassword: ''
            }}
            validationSchema={PasswordSchema}
            onSubmit={handleChangePassword}
          >
            {({ isSubmitting }) => (
              <StyledForm>
                <FormGroup>
                  <Label htmlFor="currentPassword">Текущий пароль</Label>
                  <Input
                    type="password"
                    id="currentPassword"
                    name="currentPassword"
                    placeholder="Введите текущий пароль"
                  />
                  <ErrorMessage name="currentPassword" component={Error} />
                </FormGroup>

                <FormGroup>
                  <Label htmlFor="newPassword">Новый пароль</Label>
                  <Input
                    type="password"
                    id="newPassword"
                    name="newPassword"
                    placeholder="Введите новый пароль"
                  />
                  <ErrorMessage name="newPassword" component={Error} />
                </FormGroup>

                <FormGroup>
                  <Label htmlFor="confirmPassword">Подтверждение пароля</Label>
                  <Input
                    type="password"
                    id="confirmPassword"
                    name="confirmPassword"
                    placeholder="Подтвердите новый пароль"
                  />
                  <ErrorMessage name="confirmPassword" component={Error} />
                </FormGroup>

                <Button type="submit" disabled={isSubmitting}>
                  {isSubmitting ? 'Сохранение...' : 'Изменить пароль'}
                </Button>
              </StyledForm>
            )}
          </Formik>
        </Card>
      )}

      {activeTab === 'bookings' && (
        <Card>
          <CardTitle>История бронирований</CardTitle>

          {user.bookings && user.bookings.length > 0 ? (
            <div>
              {/* Здесь будет список бронирований */}
              <p>В разработке...</p>
            </div>
          ) : (
            <NoBookings>
              <p>У вас пока нет бронирований</p>
              <a href="/rooms">Забронировать номер</a>
            </NoBookings>
          )}
        </Card>
      )}
    </Container>
  );
};

export default ProfilePage;
